<template>
  <Component
    :is        = "props.inline ? 'span' : 'div'"
    role       = "img"
    aria-label = "The ReSee Movies Logo"
    :class     = "[
      'items-center',
      {
        'flex'           : !props.inline,
        'inline-flex'    : props.inline,
        'translate-y-1'  : props.inline,
        'gap-1'          : props.size === 'xs' || props.size === 'sm',
        'gap-1 lg:gap-2' : props.size === 'lg' || props.size === 'responsive',
      },
    ]"
  >
    <Component
      v-if   = "props.showLogo"
      :is    = "props.inline ? 'span' : 'div'"
      :class = "['logo', 'aspect-square', imageSizes.logo]"
    />

    <Component
      v-if   = "props.showName"
      :is    = "props.inline ? 'span' : 'div'"
      :class = "['name', 'aspect-[3.875/1]', imageSizes.name]"
    />
  </Component>
</template>


<script lang="ts">
  /**
   * The ReseeWordLogo component displays the ReSee Movies color wheel logo,
   * and/or the "ReSee" word mark side-by-side.
   */

  export interface ReseeWordLogoProps {
    showLogo? : boolean;
    showName? : boolean;
    size?     : 'lg' | 'md' | 'sm' | 'xs' | 'responsive';
    inline?   : boolean;
  }
</script>

<script setup lang="ts">
  import { computed } from 'vue';

  const props = withDefaults(
    defineProps<ReseeWordLogoProps>(),
    {
      showLogo : true,
      showName : true,
      size     : 'responsive',
      inline   : false,
    },
  );

  const imageSizes = computed(() => {
    switch (props.size) {
      case 'lg': return { logo: 'h-10', name: 'h-8' };
      case 'md': return { logo: 'h-8', name: 'h-6' };
      case 'sm': return { logo: 'h-6', name: 'h-4' };
      case 'xs': return { logo: 'h-5', name: 'h-3' };

      default:
        return {
          logo : 'h-8 lg:h-10',
          name : 'h-6 lg:h-8',
        };
    }
  });
</script>


